    <script>
       
        function validateForm(){
        let name = document.getElementById("name").value;
        let email = document.getElementById("email").value;
        let password = document.getElementById("password").value;
        let confirmpassword = document.getElementById("confirmpassword").value;

            
        if(name === ""){
            document.getElementById('nameerror').innerText='Field is Required';
        }
        if(name.length>20){
            document.getElementById('nameerror').innerText='You exceeded 20 characters';
        }
        

        if(email === ""){
            document.getElementById('emailerror').innerText='Field is Required';
        }
        if(password === ""){
            document.getElementById('passworderror').innerText='Field is Required';
        }
        if(password.length>20){
            document.getElementById('passworderror').innerText='Invalid password';
        }
        if(password !== confirmpassword){
            document.getElementById('confirmpassworderror').innerText='Passwords Dont Match';
        }

    }
    </script>
